                if (ShouldUseSetter(mode, bindableProperty))
                {
                    setter = static (source, value) => 
                    {
                        if (value is null)
                        {
                            return;
                        }
                        
                        if (source[12] is {} p0)
                        {
                            p0["Abc"][0] = value;
